DROP DATABASE IF EXISTS ggwp;
CREATE DATABASE ggwp;
USE ggwp;
DROP USER IF EXISTS 'admin'@'localhost';
CREATE USER 'admin'@'localhost' IDENTIFIED BY 'adminadmin';
GRANT ALL ON ggwp.* TO 'admin'@'localhost';

/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `videogioco` (
  `titolo` varchar(50) NOT NULL,
  `data_pubb` date NOT NULL,
  `sviluppatore` varchar(45) DEFAULT NULL,
  `editore` varchar(45) DEFAULT NULL,
  `descrizione` varchar(1000) DEFAULT NULL,
  `prezzo` float DEFAULT NULL,
  `immagine` varchar(255) DEFAULT NULL,
  `video` varchar(255) DEFAULT NULL,
  `data_agg` date NOT NULL,
  `nAcq` int(255) DEFAULT 0,
  PRIMARY KEY (`titolo`) 
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

CREATE TABLE `utenti` (
  `email` varchar(40) NOT NULL,
  `password` varchar(50) NOT NULL,
  `tipo` varchar(5) NOT NULL,
  `nickname` varchar(45) NOT NULL,
  `nome` varchar(70) DEFAULT NULL,
  `bio` mediumtext,
  `immagine` longtext,
  `punti` int DEFAULT '0',
  `portafoglio` double DEFAULT '0',
  PRIMARY KEY (`email`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

CREATE TABLE `genere` (
  `tipo` varchar(35) NOT NULL,
  PRIMARY KEY (`tipo`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

CREATE TABLE `vgGenere`(
	`genere` varchar(35) NOT NULL,
	`gioco` varchar(50) not null,
    primary key(`gioco`, `genere`),
    foreign key(`gioco`) references videogioco(`titolo`),
	foreign key(`genere`) references genere(`tipo`)
);

CREATE TABLE `carrello` (
  `titolo` varchar(50) NOT NULL,
  `email` varchar(40) NOT NULL,
  `acquistato` tinyint(1) NOT NULL,
  `data_acquisto` date,
  `prezzo` double NOT NULL,
  `prezzo_effettivo` double,
  `immagine` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`titolo`,`email`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

